import logo from './logo.svg';
import './App.css';
import Screen from './Component/BeautifulScreen/BeautifulScreen';
import ButtonNumber from './Component/AmazingNumberButton/AmazingNumberButton';
import ButtonOperation from './Component/GreatOperationButton/GreatOperationButton';
import App from './Component/MagnificientEqualButton/MagnificientEqualButton';
import Equal from './Component/MagnificientEqualButton/MagnificientEqualButton';
import Ac from './Component/MagnificientAcButton/MagnificientEqualButton';
import Save from './Component/MagnificientSaveButton/MagnificientEqualButton';
import React, { useState } from 'react';
var stringMath = require('string-math');

const Operator = () => {
  const [number, setNumber] = useState([]);
  const [Result, setResult] = useState(0);

  const Calc = () => {
    let TabNum = [...number];
    if (isNaN(TabNum[TabNum.length -1])){
      TabNum.pop();
    }
    setResult(stringMath(TabNum.join("")));
    var temp = [];
    setNumber(temp);
  }
  const Reset = () => {
    let temp = [];
    setNumber(temp);
    setResult(0);
  }

  const changeNumb = (newNumb) => {
    if (isNaN(number[number.length -1])&& number.length > 0){
      if(isNaN(newNumb)){
        var temp = [...number];
        temp.pop();
        setNumber(temp);
        setNumber([...temp, newNumb]) 
      } else {
        setNumber([...number, newNumb])
      }
    }else {
      setNumber([...number, newNumb])
    }
    }



  return(
    <div className="container">
      <h1  className= "Title">Calculator9000</h1>
  <div className="calc-body">
     <><Screen value = {number} value2 = {Result}/><ButtonOperation numb={changeNumb}/> <ButtonNumber numb={changeNumb}/> <Equal calc = {Calc}/> <Ac reset = {Reset}/> <Save/></>
     </div>
     </div>
     );
}
export default Operator;
